componentClass:
class DynamicPluginComponent {
  title = 'Dynamically Loaded Component';
  description = 'This entire component was fetched via XHR and compiled at runtime!';
  loadedAt = new Date();
  count = signal(0);
  lastUpdated = signal(new Date().toLocaleTimeString());
  userInput = '';

  ngOnInit() {
    console.log('[Dynamic Plugin] Component initialized!');
  }

  increment() {
    this.count.update(v => v + 1);
    this.updateTimestamp();
  }

  decrement() {
    this.count.update(v => v - 1);
    this.updateTimestamp();
  }

  reset() {
    this.count.set(0);
    this.updateTimestamp();
  }

  updateTimestamp() {
    this.lastUpdated.set(new Date().toLocaleTimeString());
  }
}
